extern kcom_kernel_t sha_kernel = {
    .kmem   = cgra_kmem_sha,
    .imem   = cgra_imem_sha,
    .col_n  = CGRA_COLS,
    .in_n   = 2,
    .out_n  = 1,
    .input  = cgra_input,
    .output = cgra_output,
    .config = config,
    .func   = software,
    .check  = check,
    .name   = "Sha",
};

/****************************************************************************/
/**                                                                        **/
/*                            LOCAL FUNCTIONS                               */
/**                                                                        **/
/****************************************************************************/

void config()
{
	for(int i = 0; i < 80; i++ )
	{
		i_W_soft[i] = kcom_getRand() % (UINT_MAX - 1 - 0 + 1) + 0;
		i_W_cgra[i] = i_W_soft[i];
	}
	cgra_input[0][0] = i_W_cgra;
	cgra_input[1][0] = i_W_cgra;
	cgra_input[2][0] = i_W_cgra;
	cgra_input[3][0] = i_W_cgra;
	cgra_input[3][1] = i_W_cgra;

}

void software(void) 
{
    o_W_soft = sha_transform( i_W_soft );
}

uint32_t check(void) 
{
    uint32_t errors = 0;
    
	o_W_cgra = i_W_cgra;


#if PRINT_CGRA_RESULTS
    PRINTF("------------------------------\n");
    for( uint8_t c = 0; c < CGRA_COLS; c ++)
    {
        for( uint8_t r = 0; r < OUT_VAR_DEPTH; r++ )
        {
            PRINTF("[%d][%d]:%08x\t\t",c,r,cgra_output[c][r]);
        }
        PRINTF("\n");
    }
#endif //PRINT_CGRA_RESULTS


#if PRINT_RESULTS
        PRINTF("\nCGRA\t\tSoft\n");
#endif

    for( int i = 0; i < 80; i++ )
    {
#if PRINT_RESULTS
        PRINTF("%08x\t%08x\t%s\n",
        o_W_cgra[i],
        o_W_soft[i],
        (o_W_cgra[i] != o_W_soft[i]) ? "Wrong!" : ""
        );
#endif

        if (o_W_cgra[i] != o_W_soft[i]) {
            errors++;
        }
    }
    return errors;
}

