/****************************************************************************/
/**                                                                        **/
/*                           EXPORTED VARIABLES                             */
/**                                                                        **/
/****************************************************************************/

extern kcom_kernel_t sqrt_kernel = {
    .kmem   = cgra_kmem_sqrt,
    .imem   = cgra_imem_sqrt,
    .col_n  = CGRA_COLS,
    .in_n   = 2,
    .out_n  = 1,
    .input  = cgra_input,
    .output = cgra_output,
    .config = config,
    .func   = software,
    .check  = check,
    .name   = "Sqrt",
};

/****************************************************************************/
/**                                                                        **/
/*                            LOCAL FUNCTIONS                               */
/**                                                                        **/
/****************************************************************************/

void config()
{
	i_in_ptr_soft = kcom_getRand() % (2147483647 - 0 + 1) + 0;
	i_in_ptr_cgra = i_in_ptr_soft;
	cgra_input[1][0] = 1;
	cgra_input[1][1] = 16384;
	cgra_input[3][0] = i_in_ptr_cgra;

}

void software(void) 
{
    o_return_soft = isqrt32( i_in_ptr_soft );
}

uint32_t check(void) 
{
    uint32_t errors = 0;
    
	o_return_cgra = cgra_output[0][0];


#if PRINT_CGRA_RESULTS
    PRINTF("------------------------------\n");
    for( uint8_t c = 0; c < CGRA_COLS; c ++)
    {
        for( uint8_t r = 0; r < OUT_VAR_DEPTH; r++ )
        {
            PRINTF("[%d][%d]:%08x\t\t",c,r,cgra_output[c][r]);
        }
        PRINTF("\n");
    }
#endif //PRINT_CGRA_RESULTS


#if PRINT_RESULTS
        PRINTF("\nCGRA\t\tSoft\n");
#endif

    for( int i = 0; i < 1; i++ )
    {
#if PRINT_RESULTS
        PRINTF("%08x\t%08x\t%s\n",
        o_return_cgra,
        o_return_soft,
        (o_return_cgra != o_return_soft) ? "Wrong!" : ""
        );
#endif

        if (o_return_cgra != o_return_soft) {
            errors++;
        }
    }
    return errors;
}

